import React, { useState, useEffect } from "react";
import axios from "axios"; // Import Axios
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart } from '@fortawesome/free-regular-svg-icons'; 
import { faUpload } from '@fortawesome/free-solid-svg-icons'; 

const Events = () => {
  const [events, setEvents] = useState([]);
  const [isHovered, setIsHovered] = useState(false);
  useEffect(() => {
    // Fetch events data from the API using Axios
    axios
      .get("http://127.0.0.1:8000/api/events/")
      .then((response) => {
        setEvents(response.data); // Update the state with fetched events
      })
      .catch((error) => {
        console.error("Error fetching events:", error);
      });
  }, []); // Empty dependency array ensures the effect runs only once

  return (
    <div className="container">
      <h2 className="mt-4 mb-4">Events</h2>
      <div className="row">
        {events.map((event) => (
          <div className='col-md-3 mb-4 ' key={event.id}>
            <div className="card border-0">
              <div className="position-relative" style={{ backgroundImage: `url(${event.image})`, backgroundSize: "cover", height: "200px" }}
              onMouseEnter={() => setIsHovered(true)}
              onMouseLeave={() => setIsHovered(false)}
              >
                <div className="position-absolute bottom-buttons  d-flex flex-row  justify-content-between align-items-center" style={{ bottom: "10px", right: "10px", color: "white",visibility: isHovered ? "visible" : "hidden"}}>
                  <div className="like d-flex flex-row  justify-content-center align-items-center">
                  <FontAwesomeIcon icon={faHeart} className="mr-2" />
                  </div>
                  <div className="like d-flex flex-row  justify-content-center align-items-center">
                  <FontAwesomeIcon icon={faUpload} />
                  </div>
                </div>
              </div>
              <div className="card-body">
                <h5 className="card-title">{event.title}</h5>
                <p className="card-text">{event.datetime}</p>
                <p className="card-text">{event.location}</p>
                <p className="card-text">From ${event.price}</p>
                <p className="card-text">{event.organizer}</p>
                <p className="card-text">{event.type}</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Events;
