import React, { useState, useEffect } from "react";
import axios from "axios"; // Import Axios
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart, } from '@fortawesome/free-regular-svg-icons'; 
import { faUpload } from '@fortawesome/free-solid-svg-icons'; 
import { faEllipsis } from '@fortawesome/free-solid-svg-icons'; 

const Events = () => {
  const [events, setEvents] = useState([]);
  const [hoveredCards, setHoveredCards] = useState([]);

  useEffect(() => {
    // Fetch events data from the API using Axios
    axios
      .get("http://127.0.0.1:8000/api/events/")
      .then((response) => {
        setEvents(response.data); // Update the state with fetched events
        setHoveredCards(new Array(response.data.length).fill(false)); // Initialize the hoveredCards array
      })
      .catch((error) => {
        console.error("Error fetching events:", error);
      });
  }, []); // Empty dependency array ensures the effect runs only once

  const handleMouseEnter = (index) => {
    setHoveredCards((prev) => {
      const newState = [...prev];
      newState[index] = true;
      return newState;
    });
  };



  const handleLike = (index) => {
    const eventId = events[index].id;
    

    axios.post(`http://127.0.0.1:8000/api/events/${eventId}/like/`)
      .then((response) => {
        console.log("Liked event:", response.data);
      })
      .catch((error) => {
        console.error("Error liking event:", error);
        
      });
  };

  return (
    <div className="container">
      <h2 className="mt-4 mb-4">Events</h2>
      <div className="row row-cols-1 row-cols-md-2 row-cols-lg-3 row-cols-xl-4">
        {events.map((event, index) => (
          <div className='col mb-4' key={event.id}>
            <div className="card border-0" style={{ minWidth: "300px" }}>
              <div 
                className="position-relative" 
                style={{ 
                  backgroundImage: `url(${event.image})`, 
                  backgroundSize: "cover", 
                  height: "200px" 
                }}
                
              >
                <div 
                  className="position-absolute bottom-buttons d-flex justify-content-between align-items-center" 
                  style={{ 
                    bottom: "10px", 
                    right: "10px", 
                    color: "white",
                    visibility: hoveredCards[index] ? "visible" : "hidden"
                  }}
                >
                  <div className="like d-flex justify-content-center align-items-center" onClick={() => handleLike(index)}>
                    <FontAwesomeIcon icon={faHeart} className="mr-2" style={{color:color}}/>
                  </div>
                  <div className="like d-flex justify-content-center align-items-center">
                    <FontAwesomeIcon icon={faUpload} />
                  </div>
                  <div className="like d-flex justify-content-center align-items-center">
                    <FontAwesomeIcon icon={faEllipsis} />
                  </div>
                </div>
              </div>
              <div className="card-body">
                <h5 className="card-title">{event.title}</h5>
                <p className="card-text">{event.datetime}</p>
                <p className="card-text">{event.location}</p>
                {event.type !== "free" && <p className="card-text">From ${event.price}</p>}
                <p className="card-text">{event.organizer}</p>
                <p className="card-text">{event.type}</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Events;
