import React, { useState, useEffect } from "react";
import axios from "axios";
import Navbar from "./Navbar";

const LikedEvents = ({ token }) => {
  const [likedEvents, setLikedEvents] = useState([]);

  useEffect(() => {
    const config = {};
    if (token) {
      config.headers = { Authorization: `Token ${token}` };
    }

    axios
      .get("http://127.0.0.1:8000/api/liked-events/", config)
      .then((response) => {
        setLikedEvents(response.data);
      })
      .catch((error) => {
        console.error("Error fetching liked events:", error);
      });
  }, [token]);

  return (
    <div className="container">
        <Navbar/>
      <h2 className="mt-4 mb-4">Liked Events</h2>
      <div className="row">
        {likedEvents.map((event) => (
          <div className="col-md-3 mb-4" key={event.id}>
            <div className="card border-0">
              {/* Render your liked event card here */}
              <div className="card-body">
                <h5 className="card-title">{event.title}</h5>
                <p className="card-text">{event.datetime}</p>
                <p className="card-text">{event.location}</p>
                <p className="card-text">From ${event.price}</p>
                <p className="card-text">{event.organizer}</p>
                <p className="card-text">{event.type}</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default LikedEvents;
