import React, { useState, useEffect } from "react";
import axios from "axios"; // Import Axios
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart } from '@fortawesome/free-regular-svg-icons'; 
import { faUpload} from '@fortawesome/free-solid-svg-icons'; 
const Events = () => {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    // Fetch events data from the API using Axios
    axios
      .get("http://127.0.0.1:8000/api/events/")
      .then((response) => {
        setEvents(response.data); // Update the state with fetched events
      })
      .catch((error) => {
        console.error("Error fetching events:", error);
      });
  }, []); // Empty dependency array ensures the effect runs only once

  return (
    <div className="events">
      <h2>Events</h2>
      <div className=" d-flex flex-row flex-wrap" >
        {events.map((event) => (
          <div className='col- event-card'key={event.id} style={{ height: "450px", width: "319px", marginRight:'16px',padding:'10px'}}>
            <div style={{ backgroundImage: `url(${event.image})`, height: "45%", width: "100%", backgroundSize: "cover" , borderRadius:'8px', marginBottom:'10px',position: "relative" }} alt={event.title} >
            <div className="bottum-buttons" style={{position: "absolute", bottom: "5px", right: "5px",
                  width: "28px",height: "28px"}}>
                <div className="like-button">
                <FontAwesomeIcon icon={faHeart} />
              </div>
              <div className="like-button">
              <svg id="share-ios-chunky_svg__eds-icon--share-ios-chunky_svg" x="20" y="43" viewBox="0 0 24 24">
              <path id="share-ios-chunky_svg__eds-icon--share-ios-chunky_base" fillRule="evenodd" clipRule="evenodd" d="M18 16v2H6v-2H4v4h16v-4z"></path>
              <path id="share-ios-chunky_svg__eds-icon--share-ios-chunky_arrow" fillRule="evenodd" clipRule="evenodd" d="M12 4L7 9l1.4 1.4L11 7.8V16h2V7.8l2.6 2.6L17 9l-5-5z"></path>
            </svg>

              </div>
            
            </div>
            
            </div>
            <h5 style={{fontWeight:400}}>{event.title}</h5>
            <span> {event.datetime}</span>
            <span> {event.location}</span>
            <span>From ${event.price}</span>
            <span> {event.organizer}</span>
            <span> {event.type}</span>
            
          </div>
        ))}
      </div>
    </div>
  );
};

export default Events;
