import React, { useState, useEffect } from "react";
import axios from "axios";
import Navbar from "./Navbar";

const LikedEvents = ({ token }) => {
  const [likedEvents, setLikedEvents] = useState([]);

  useEffect(() => {
    axios
      .get("http://127.0.0.1:8000/api/liked-events/", {
        headers: {
          Authorization: `Token ${localStorage.getItem('token')}`
        }
      })
      .then((response) => {
        setLikedEvents(response.data);
      })
      .catch((error) => {
        console.error("Error fetching liked events:", error);
      });
  }, [token]);

  return (
    <div className="">
      <Navbar />
      <div className="container">
        <h2 className="mt-4 mb-4" style={{fontWeight:'600'}}>Liked Events</h2>
        <div className="row">
          {likedEvents.map((event) => (
            <div className="col-md-12 mb-4" key={event.id}>
              <div className="card border-0">
                <div className="row">
                  <div className="col-md-8">
                    <div className="card-body">
                      <h5 className="card-title">{event.title}</h5>
                      <p className="card-text">{event.datetime}</p>
                      <p className="card-text">{event.location}</p>
                      <p className="card-text">From ${event.price}</p>
                      <p className="card-text">{event.organizer}</p>
                      <p className="card-text">{event.type}</p>
                    </div>
                  </div>
                  <div className="col-md-4 p-2">
                    <img
                      src={event.image}
                      className="img-fluid"
                      alt="Event"
                    />
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default LikedEvents;
