import Logo from "./Logo";
import React, { useState } from "react";
import axios from "axios";
import {useNavigate } from 'react-router-dom'

const Signup = () => {
  const [formData, setFormData] = useState({
    username: "",
    // confirmEmail: "",
    first_name: "",
    last_name: "",
    password: ""
  });
const navigate = useNavigate();
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://127.0.0.1:8000/api/signup/", formData);
      console.log("Signup successful:", response.data);
      const token = response.data.token;
      localStorage.setItem("token", token);
      
      alert('Success Login',token);
      navigate('/')
    } catch (error) {
      console.error("Signup error:", error);
      // Handle error, e.g., show error message to user
    }
  };

  return (
    <div className="login container-fluid">
      <div className="login-content">
        <div className="login-split-content">
          <a href="/"><Logo /></a>
          <h1 style={{ fontWeight: '1000', fontSize: '50px', color: '#39364F' }} className="mb-4">Create an account</h1>
          <form onSubmit={handleSubmit}>
            <div className="container">
              <input
                type="text"
                name="username"
                placeholder="Email address"
                className="input form-control mt-3"
                value={formData.username}
                onChange={handleChange}
              />
              <input
                type="text"
                name="confirmEmail"
                placeholder="Confirm Email address"
                className="input form-control mt-3"
                value={formData.confirmEmail}
                onChange={handleChange}
              />
              <div className="row">
                <div className="col">
                  <input
                    type="text"
                    name="first_name"
                    placeholder="First Name"
                    style={{ height: '60px', padding: '12px', borderRadius: '0' }}
                    className="form-control mt-3"
                    value={formData.first_name}
                    onChange={handleChange}
                  />
                </div>
                <div className="col">
                  <input
                    type="text"
                    name="last_name"
                    placeholder="Last Name"
                    style={{ height: '60px', padding: '12px', borderRadius: '0' }}
                    className="form-control mt-3"
                    value={formData.last_name}
                    onChange={handleChange}
                  />
                </div>
              </div>
              <input
                type="password"
                name="password"
                placeholder="Password"
                className="input form-control mt-3"
                value={formData.password}
                onChange={handleChange}
              />
              <button type="submit" className="submit mt-3 mb-4">Continue</button>
            </div>
          </form>
          <button type="submit" className="google form-control mb-2">
            <div className="img"></div>
            Sign up with Google
          </button>
          <span className="mt-3">
            Existing user ? <a href="/login" className="text-decoration-none">login</a>
          </span>
        </div>
      </div>
      <div className="login-image"></div>
    </div>
  );
};

export default Signup;
