import React, { useState, useEffect } from "react";
import axios from "axios"; // Import Axios

const Events = () => {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    // Fetch events data from the API using Axios
    axios
      .get("http://127.0.0.1:8000/api/events/")
      .then((response) => {
        setEvents(response.data); // Update the state with fetched events
      })
      .catch((error) => {
        console.error("Error fetching events:", error);
      });
  }, []); // Empty dependency array ensures the effect runs only once

  return (
    <div className="events">
      <h2>Events</h2>
      <div className=" d-flex flex-row flex-wrap" >
        {events.map((event) => (
          <div className='col- event-card'key={event.id} style={{ height: "450px", width: "319px", marginRight:'16px',padding:'10px'}}>
            <div style={{ backgroundImage: `url(${event.image})`, height: "45%", width: "100%", backgroundSize: "cover" , borderRadius:'8px', marginBottom:'10px',position: "relative" }} alt={event.title} >

            <svg
                id="heart-chunky_svg__eds-icon--user-chunky_svg"
                x="0"
                y="0"
                viewBox="0 0 24 24"
                xmlSpace="preserve"
                style={{
                  position: "absolute", // Add position absolute to position the SVG within the div
                  bottom: "5px", // Adjust the top position as needed
                  right: "5px", // Adjust the right position as needed
                  width: "24px", // Set the width of the SVG
                  height: "24px", // Set the height of the SVG
                  marginBottom:"auto",
                  
                }}
              >
                <path
                  id="heart-chunky_svg__eds-icon--heart-chunky_base"
                  fillRule="evenodd"
                  clipRule="evenodd"
                  d="M18.8 6.2C18.1 5.4 17 5 16 5c-1 0-2 .4-2.8 1.2L12 7.4l-1.2-1.2C10 5.4 9 5 8 5c-1 0-2 .4-2.8 1.2-1.5 1.6-1.5 4.2 0 5.8l6.8 7 6.8-7c1.6-1.6 1.6-4.2 0-5.8zm-1.4 4.4L12 16.1l-5.4-5.5c-.8-.8-.8-2.2 0-3C7 7.2 7.5 7 8 7c.5 0 1 .2 1.4.6l2.6 2.7 2.7-2.7c.3-.4.8-.6 1.3-.6s1 .2 1.4.6c.8.8.8 2.2 0 3z"
                ></path>
              </svg>
            </div>
            <h5 style={{fontWeight:400}}>{event.title}</h5>
            <span> {event.datetime}</span>
            <span> {event.location}</span>
            <span>From ${event.price}</span>
            <span> {event.organizer}</span>
            <span> {event.type}</span>
            
          </div>
        ))}
      </div>
    </div>
  );
};

export default Events;
