import React, { useState, useEffect } from "react";
import './Home.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMagnifyingGlass, faLocationDot } from '@fortawesome/free-solid-svg-icons'; 
import axios from 'axios';

const Navbar = () => {
    const [userData, setUserData] = useState(null);
    const token = localStorage.getItem('token');

    useEffect(() => {
        // Fetch user data only if token exists
        if (token) {
            axios.get('http://127.0.0.1:8000/api/user/')
                .then(response => {
                    // Update the state with the user data
                    setUserData(response.data);
                })
                .catch(error => {
                    console.error('Error fetching user data:', error);
                });
        }
    }, [token]); // Fetch user data when token changes

    return (
        <div className="navbar navbar-expand-lg navbar-light bg-ligh">
            <div className="container-fluid">
                <a className="navbar-brand" href="#">
                    {/* Your Branding */}
                </a>
                <div className="search">
                    <div className="search-icon">
                        <FontAwesomeIcon className="icon" icon={faMagnifyingGlass} />
                        Search
                    </div>
                    <div className="location">
                        <FontAwesomeIcon className='loc' icon={faLocationDot} /> Cochin
                    </div>
                </div>
                <div className="links">
                    {userData ? (
                        <>
                            {/* Display user data */}
                            <span>{userData.username}</span>
                        </>
                    ) : (
                        <>
                            {/* Display default links when user data is not available */}
                            <a href=''><span>Find Events</span></a>
                            <a href=''><span>Create Events</span></a>
                            <a href=''><span>Help Center</span></a>
                            <a href='/login'><span>Log In</span></a>
                            <a href='/signup'><span>Sign Up</span></a>
                        </>
                    )}
                </div>
            </div>
        </div>
    );
}

export default Navbar;
