import React, { useState, useEffect } from "react";
import './Home.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMagnifyingGlass, faLocationDot } from '@fortawesome/free-solid-svg-icons'; 
import { faUser } from '@fortawesome/free-regular-svg-icons'; 
import axios from 'axios';

const Navbar = () => {
    const [userData, setUserData] = useState(null);
    const token = localStorage.getItem('token');

    useEffect(() => {
        // Fetch user data only if token exists
        if (token) {
            axios.get('http://127.0.0.1:8000/api/user/', {
                headers: {
                    Authorization: `Token ${token}`
                }
            })
            .then(response => {
                // Update the state with the user data
                setUserData(response.data);
            })
            .catch(error => {
                console.error('Error fetching user data:', error);
            });
        }
    }, [token]); // Fetch user data when token changes

    return (
        <div className="navbar navbar-expand-lg navbar-light bg-ligh">
            <div className="container-fluid">
                <a className="navbar-brand" href="#">
                </a>
                <div className="search">
                    <div className="search-icon">
                        <FontAwesomeIcon className="icon" icon={faMagnifyingGlass} />
                        Search
                    </div>
                    <div className="location">
                        <FontAwesomeIcon className='loc' icon={faLocationDot} /> Cochin
                    </div>
                </div>
                <div className="links">
                    {localStorage.getItem('token') ? (
                        <>
                           {userData && <div className="user-items d-flex justify-content-between align-items-center">
                           <span>Create an event</span>
                            <span>Likes</span>
                            <span>Tickets</span>
                           <span href="#" className="text-decoration-none profile-nav" >
                           <FontAwesomeIcon icon={faUser} /> <span >{userData.email}</span></span>
                           </div>
                           }
                            
                        </>
                    ) : (
                        <>
                           
                            <a href='' className="text-decoration-none"><span>Find Events</span></a>
                            <a href=''className="text-decoration-none"><span>Create Events</span></a>
                            <a href='' className="text-decoration-none"><span>Help Center</span></a>
                            <a href='/login' className="text-decoration-none"><span>Log In</span></a>
                            <a href='/signup' className="text-decoration-none"><span>Sign Up</span></a>
                        </>
                    )}
                </div>
            </div>
        </div>
    );
}

export default Navbar;
