import React, { useState, useEffect } from "react";
import axios from "axios"; // Import Axios

const Events = () => {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    // Fetch events data from the API using Axios
    axios
      .get("http://127.0.0.1:8000/api/events/")
      .then((response) => {
        setEvents(response.data); // Update the state with fetched events
      })
      .catch((error) => {
        console.error("Error fetching events:", error);
      });
  }, []); // Empty dependency array ensures the effect runs only once

  return (
    <div className="events">
      <h2>Events</h2>
      <div className=" d-flex flex-row">
        {events.map((event) => (
          <div key={event.id}>
            <div style={{ backgroundImage: `url(${event.image})`, height: "170px", width: "340px", backgroundSize: "cover" }} alt={event.title}>
            </div>
            <h3>{event.title}</h3>
            <p>Date: {event.datetime}</p>
            <p>Location: {event.location}</p>
            <p>Price: ${event.price}</p>
            <p>Organizer: {event.organizer}</p>
            <p>Type: {event.type}</p>
            
          </div>
        ))}
      </div>
    </div>
  );
};

export default Events;
