import React, { useState, useEffect } from "react";
import axios from "axios"; // Import Axios
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart } from '@fortawesome/free-regular-svg-icons'; 
import { faUpload } from '@fortawesome/free-solid-svg-icons'; 

const Events = () => {
  const [events, setEvents] = useState([]);
  const [isHovered, setIsHovered] = useState(false); // Define isHovered state

  useEffect(() => {
    // Fetch events data from the API using Axios
    axios
      .get("http://127.0.0.1:8000/api/events/")
      .then((response) => {
        setEvents(response.data); // Update the state with fetched events
      })
      .catch((error) => {
        console.error("Error fetching events:", error);
      });
  }, []); // Empty dependency array ensures the effect runs only once

  return (
    <div className="container">
      <h2 className="mt-4 mb-4">Events</h2>
      <div className="row">
        {events.map((event) => (
          <div className='col-md-3 mb-4 ' key={event.id}>
            <div className="card border-0 position-relative" onMouseEnter={() => setIsHovered(true)} onMouseLeave={() => setIsHovered(false)}>
              <img src={event.image} className="card-img-top" alt={event.title} />
              <div className={`card-img-overlay ${isHovered ? 'visible' : 'invisible'}`}>
                <FontAwesomeIcon icon={faHeart} className="mr-2" />
                <FontAwesomeIcon icon={faUpload} />
              </div>
              <div className="card-body">
                <h5 className="card-title">{event.title}</h5>
                <p className="card-text">{event.datetime}</p>
                <p className="card-text">{event.location}</p>
                <p className="card-text">From ${event.price}</p>
                <p className="card-text">{event.organizer}</p>
                <p className="card-text">{event.type}</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Events;
